import * as types from "../actions/ActionTypes";

const INITIAL_STATE = {
  counters: [
    {
      color: "black",
      number: 0
    }
  ]
};

function counters(state = INITIAL_STATE, action) {
  switch (action.type) {
    case types.CREATE:
      return {
        counters: [
          ...counters,
          {
            color: action.color,
            number: 0
          }
        ]
      };
    case types.REMOVE:
      return {
        counters: counters.slice(0, counters.length - 1)
      };
    default:
      return state;
  }
}
