import { connect } from "react-redux";
import Counter from "../components/Counter.component";

const mapStateToProps = state => ({
  number: state.number,
  color: state.color
});

const mapDispatchToProps = dispatch => ({
  onIncrement: () =>
    dispatch({
      type: "INCREMENT"
    }),
  onDecrement: () =>
    dispatch({
      type: "DECREMENT"
    })
});
const CounterContainer = connect()(Counter);
